import React, { useState } from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import IngredientInput from './components/IngredientInput';
import RecipeList from './components/RecipeList';
import RecipeDetail from './components/RecipeDetail';
import FilterPanel from './components/FilterPanel';
import LoadingSpinner from './components/LoadingSpinner';
import { recipeService } from './services/recipeService';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`;

const MainContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  
  @media (max-width: 768px) {
    padding: 10px;
  }
`;

const ContentGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 3fr;
  gap: 20px;
  margin-top: 20px;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 15px;
  }
`;

const SearchSection = styled.div`
  background: white;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  height: fit-content;
  
  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const ResultsSection = styled.div`
  background: white;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  min-height: 400px;
  
  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const ErrorMessage = styled.div`
  background: #fee;
  border: 1px solid #fcc;
  border-radius: 8px;
  padding: 12px;
  color: #c00;
  margin: 10px 0;
`;

function App() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedIngredients, setSelectedIngredients] = useState([]);
  const [filters, setFilters] = useState({
    dietary: [],
    difficulty: '',
    maxCookingTime: '',
    maxCalories: '',
    cuisine: ''
  });

  const handleIngredientsChange = (ingredients) => {
    setSelectedIngredients(ingredients);
  };

  const handleFiltersChange = (newFilters) => {
    setFilters(newFilters);
  };

  const searchRecipes = async () => {
    if (selectedIngredients.length === 0) {
      setError('Please add at least one ingredient');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await recipeService.searchByIngredients(selectedIngredients);
      setRecipes(response.recipes);
      
      if (response.recipes.length === 0) {
        setError('No recipes found with the selected ingredients. Try different ingredients or check the substitution suggestions.');
      }
    } catch (err) {
      setError('Failed to search recipes. Please try again.');
      console.error('Search error:', err);
    } finally {
      setLoading(false);
    }
  };

  const filterRecipes = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await recipeService.filterRecipes(filters);
      setRecipes(response.recipes);
      
      if (response.recipes.length === 0) {
        setError('No recipes match your filter criteria. Try adjusting your filters.');
      }
    } catch (err) {
      setError('Failed to filter recipes. Please try again.');
      console.error('Filter error:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadAllRecipes = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await recipeService.getAllRecipes();
      setRecipes(response.recipes);
    } catch (err) {
      setError('Failed to load recipes. Please try again.');
      console.error('Load error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Router>
      <AppContainer>
        <Header />
        <MainContent>
          <Routes>
            <Route path="/" element={
              <>
                <ContentGrid>
                  <SearchSection>
                    <IngredientInput
                      selectedIngredients={selectedIngredients}
                      onIngredientsChange={handleIngredientsChange}
                      onSearch={searchRecipes}
                    />
                    
                    <FilterPanel
                      filters={filters}
                      onFiltersChange={handleFiltersChange}
                      onFilter={filterRecipes}
                      onShowAll={loadAllRecipes}
                    />
                  </SearchSection>
                  
                  <ResultsSection>
                    {error && <ErrorMessage>{error}</ErrorMessage>}
                    
                    {loading ? (
                      <LoadingSpinner />
                    ) : (
                      <RecipeList recipes={recipes} />
                    )}
                  </ResultsSection>
                </ContentGrid>
              </>
            } />
            <Route path="/recipe/:id" element={<RecipeDetail />} />
          </Routes>
        </MainContent>
      </AppContainer>
    </Router>
  );
}

export default App;
