import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FaPlus, FaTimes, FaSearch } from 'react-icons/fa';
import { recipeService } from '../services/recipeService';

const Container = styled.div`
  margin-bottom: 20px;
`;

const Title = styled.h3`
  margin: 0 0 20px 0;
  color: #2d3748;
  font-size: 20px;
  font-weight: 700;
  display: flex;
  align-items: center;
  gap: 10px;
  
  &::before {
    content: 'ðŸ¥•';
    font-size: 24px;
  }
`;

const InputContainer = styled.div`
  position: relative;
  margin-bottom: 15px;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px 40px 12px 12px;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 14px;
  outline: none;
  transition: border-color 0.2s;
  
  &:focus {
    border-color: #667eea;
  }
  
  &::placeholder {
    color: #a0aec0;
  }
`;

const AddButton = styled.button`
  position: absolute;
  right: 8px;
  top: 50%;
  transform: translateY(-50%);
  background: #667eea;
  color: white;
  border: none;
  border-radius: 6px;
  padding: 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.2s;
  
  &:hover {
    background: #5a67d8;
  }
  
  &:disabled {
    background: #cbd5e0;
    cursor: not-allowed;
  }
`;

const SuggestionsDropdown = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  background: white;
  border: 1px solid #e2e8f0;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  max-height: 200px;
  overflow-y: auto;
  z-index: 10;
`;

const SuggestionItem = styled.div`
  padding: 10px 12px;
  cursor: pointer;
  border-bottom: 1px solid #f7fafc;
  transition: background-color 0.2s;
  
  &:hover {
    background-color: #f7fafc;
  }
  
  &:last-child {
    border-bottom: none;
  }
`;

const SelectedIngredients = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-bottom: 15px;
`;

const IngredientChip = styled.div`
  display: flex;
  align-items: center;
  gap: 6px;
  background: #667eea;
  color: white;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 14px;
  font-weight: 500;
`;

const RemoveButton = styled.button`
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  padding: 0;
  display: flex;
  align-items: center;
  
  &:hover {
    color: #fbb6ce;
  }
`;

const SearchButton = styled.button`
  width: 100%;
  background: #48bb78;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  transition: background-color 0.2s;
  
  &:hover {
    background: #38a169;
  }
  
  &:disabled {
    background: #cbd5e0;
    cursor: not-allowed;
  }
`;

const IngredientInput = ({ selectedIngredients = [], onIngredientsChange, onSearch }) => {
  const [inputValue, setInputValue] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  useEffect(() => {
    const fetchSuggestions = async () => {
      if (inputValue.trim().length > 1) {
        try {
          const response = await recipeService.getIngredientSuggestions(inputValue);
          setSuggestions(response.suggestions || []);
          setShowSuggestions(true);
        } catch (error) {
          setSuggestions([]);
        }
      } else {
        setSuggestions([]);
        setShowSuggestions(false);
      }
    };

    const debounceTimer = setTimeout(fetchSuggestions, 300);
    return () => clearTimeout(debounceTimer);
  }, [inputValue]);

  const addIngredient = (ingredient) => {
    const ingredientName = typeof ingredient === 'string' ? ingredient : ingredient.name;
    if (ingredientName && !selectedIngredients.includes(ingredientName)) {
      onIngredientsChange([...selectedIngredients, ingredientName]);
    }
    setInputValue('');
    setShowSuggestions(false);
  };

  const removeIngredient = (ingredient) => {
    onIngredientsChange(selectedIngredients.filter(item => item !== ingredient));
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      if (inputValue.trim()) {
        addIngredient(inputValue.trim());
      }
    }
  };

  const handleSuggestionClick = (suggestion) => {
    addIngredient(suggestion);
  };

  return (
    <Container>
      <Title>Available Ingredients</Title>
      
      <InputContainer>
        <Input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type an ingredient (e.g., chicken, tomato, rice...)"
          onFocus={() => inputValue.length > 1 && setShowSuggestions(true)}
        />
        <AddButton 
          onClick={() => addIngredient(inputValue)}
          disabled={!inputValue.trim()}
        >
          <FaPlus />
        </AddButton>
        
        {showSuggestions && suggestions.length > 0 && (
          <SuggestionsDropdown>
            {suggestions.slice(0, 8).map((suggestion, index) => (
              <SuggestionItem
                key={index}
                onClick={() => handleSuggestionClick(suggestion)}
              >
                {typeof suggestion === 'string' ? suggestion : suggestion.name}
              </SuggestionItem>
            ))}
          </SuggestionsDropdown>
        )}
      </InputContainer>

      {selectedIngredients.length > 0 && (
        <SelectedIngredients>
          {selectedIngredients.map((ingredient, index) => (
            <IngredientChip key={index}>
              {ingredient}
              <RemoveButton onClick={() => removeIngredient(ingredient)}>
                <FaTimes />
              </RemoveButton>
            </IngredientChip>
          ))}
        </SelectedIngredients>
      )}

      <SearchButton 
        onClick={onSearch}
        disabled={selectedIngredients.length === 0}
      >
        <FaSearch />
        Find Recipes
      </SearchButton>
    </Container>
  );
};

export default IngredientInput;
