import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import styled from 'styled-components';
import { FaClock, FaUsers, FaStar, FaArrowLeft, FaLeaf } from 'react-icons/fa';
import ReactRating from 'react-rating-stars-component';
import { recipeService } from '../services/recipeService';
import LoadingSpinner from './LoadingSpinner';

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: white;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  overflow: hidden;
`;

const BackButton = styled(Link)`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 20px;
  color: #667eea;
  text-decoration: none;
  font-weight: 500;
  
  &:hover {
    color: #5a67d8;
  }
`;

const RecipeHeader = styled.div`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 40px;
  text-align: center;
  position: relative;
`;

const RecipeTitle = styled.h1`
  margin: 0 0 10px 0;
  font-size: 28px;
  font-weight: 700;
`;

const RecipeDescription = styled.p`
  margin: 0 0 20px 0;
  font-size: 16px;
  opacity: 0.9;
  line-height: 1.5;
`;

const RecipeStats = styled.div`
  display: flex;
  justify-content: center;
  gap: 30px;
  margin-bottom: 20px;
  
  @media (max-width: 768px) {
    flex-wrap: wrap;
    gap: 20px;
  }
`;

const Stat = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 16px;
`;

const DifficultyBadge = styled.span`
  background: rgba(255, 255, 255, 0.2);
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 14px;
  font-weight: 600;
  text-transform: capitalize;
`;

const ContentSection = styled.div`
  padding: 30px 40px;
  
  @media (max-width: 768px) {
    padding: 20px;
  }
`;

const SectionTitle = styled.h3`
  margin: 0 0 15px 0;
  color: #2d3748;
  font-size: 20px;
  border-bottom: 2px solid #e2e8f0;
  padding-bottom: 10px;
`;

const IngredientsList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0 0 30px 0;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 8px;
`;

const IngredientItem = styled.li`
  background: #f7fafc;
  padding: 10px 15px;
  border-radius: 8px;
  border-left: 4px solid #667eea;
  font-weight: 500;
`;

const InstructionsList = styled.ol`
  padding-left: 0;
  margin: 0 0 30px 0;
`;

const InstructionItem = styled.li`
  background: #f9f9f9;
  margin: 0 0 15px 0;
  padding: 15px;
  border-radius: 8px;
  border-left: 4px solid #48bb78;
  line-height: 1.6;
  counter-increment: step-counter;
  
  &::before {
    content: counter(step-counter);
    background: #48bb78;
    color: white;
    font-weight: bold;
    padding: 4px 8px;
    border-radius: 50%;
    margin-right: 12px;
    font-size: 14px;
    min-width: 24px;
    text-align: center;
    display: inline-block;
  }
`;

const DietaryTags = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-bottom: 20px;
`;

const DietaryTag = styled.span`
  background: #e6fffa;
  color: #234e52;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 14px;
  font-weight: 500;
  display: flex;
  align-items: center;
  gap: 6px;
`;

const RatingSection = styled.div`
  border-top: 2px solid #e2e8f0;
  padding-top: 20px;
  text-align: center;
`;

const RatingTitle = styled.h4`
  margin: 0 0 15px 0;
  color: #4a5568;
`;

const CurrentRating = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  margin-bottom: 15px;
  font-size: 16px;
  color: #4a5568;
`;

const ErrorMessage = styled.div`
  background: #fee;
  border: 1px solid #fcc;
  border-radius: 8px;
  padding: 20px;
  color: #c00;
  text-align: center;
  margin: 20px;
`;

const RecipeDetail = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRating, setUserRating] = useState(0);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        setLoading(true);
        const response = await recipeService.getRecipeById(id);
        setRecipe(response.recipe);
      } catch (err) {
        setError('Failed to load recipe details');
        console.error('Recipe fetch error:', err);
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchRecipe();
    }
  }, [id]);

  const handleRating = async (rating) => {
    try {
      await recipeService.rateRecipe(id, rating);
      setUserRating(rating);
      // Refresh recipe data to get updated average rating
      const response = await recipeService.getRecipeById(id);
      setRecipe(response.recipe);
    } catch (err) {
      console.error('Rating error:', err);
    }
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage>{error}</ErrorMessage>;
  }

  if (!recipe) {
    return <ErrorMessage>Recipe not found</ErrorMessage>;
  }

  const formatTime = (minutes) => {
    if (minutes < 60) return `${minutes}m`;
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return mins > 0 ? `${hours}h ${mins}m` : `${hours}h`;
  };

  return (
    <div>
      <BackButton to="/">
        <FaArrowLeft />
        Back to Recipes
      </BackButton>
      
      <Container>
        <RecipeHeader>
          <RecipeTitle>{recipe.name}</RecipeTitle>
          <RecipeDescription>{recipe.description}</RecipeDescription>
          
          <RecipeStats>
            <Stat>
              <FaClock />
              {formatTime(recipe.totalTime || recipe.cookingTime || 30)}
            </Stat>
            <Stat>
              <FaUsers />
              {recipe.servings || 4} servings
            </Stat>
            <Stat>
              <FaStar />
              {Math.round((recipe.averageRating || 0) * 10) / 10}
            </Stat>
            <DifficultyBadge>
              {recipe.difficulty}
            </DifficultyBadge>
          </RecipeStats>
          
          {recipe.dietary && recipe.dietary.length > 0 && (
            <DietaryTags>
              {recipe.dietary.map((diet, index) => (
                <DietaryTag key={index}>
                  <FaLeaf />
                  {diet}
                </DietaryTag>
              ))}
            </DietaryTags>
          )}
        </RecipeHeader>

        <ContentSection>
          <SectionTitle>Ingredients</SectionTitle>
          <IngredientsList>
            {recipe.ingredients && recipe.ingredients.map((ingredient, index) => (
              <IngredientItem key={index}>
                {ingredient.amount} {ingredient.unit} {ingredient.name}
              </IngredientItem>
            ))}
          </IngredientsList>

          <SectionTitle>Instructions</SectionTitle>
          <InstructionsList>
            {recipe.instructions && recipe.instructions.map((instruction, index) => (
              <InstructionItem key={index}>
                {instruction}
              </InstructionItem>
            ))}
          </InstructionsList>

          <RatingSection>
            <RatingTitle>Rate this Recipe</RatingTitle>
            <CurrentRating>
              <span>Current Rating:</span>
              <FaStar style={{ color: '#ffd700' }} />
              {Math.round((recipe.averageRating || 0) * 10) / 10} / 5
            </CurrentRating>
            
            <ReactRating
              count={5}
              onChange={handleRating}
              size={30}
              activeColor="#ffd700"
              value={userRating}
            />
          </RatingSection>
        </ContentSection>
      </Container>
    </div>
  );
};

export default RecipeDetail;
